mapper.select:
select count(*) from solution_config
and module = #{module}
and (name like concat('%',#{searchInput},'%') or intro like concat('%',#{searchInput},'%') or creator like concat('%',#{searchInput},'%') or version like concat('%',#{searchInput},'%') or module like concat('%',#{searchInput},'%'))

select * from solution_config
and module = #{module}
and (name like concat('%',#{searchInput},'%') or intro like concat('%',#{searchInput},'%') or creator like concat('%',#{searchInput},'%') or version like concat('%',#{searchInput},'%') or module like concat('%',#{searchInput},'%'))
limit #{page.start},#{page.limit}
